CREATE TABLE "SYS"."USERSS" 
(   "USERNAME" VARCHAR2(255 CHAR) NOT NULL ENABLE, 
    "FIRST_NAME" VARCHAR2(255 CHAR), 
    "LAST_NAME" VARCHAR2(255 CHAR), 
    "PASSWORD" VARCHAR2(255 CHAR), 
    "ROLE" VARCHAR2(255 CHAR), 
    PRIMARY KEY ("USERNAME")
);

CREATE TABLE "SYS"."CATEGORY" 
(   "CATEGORY_ID" NUMBER(19,0) GENERATED BY DEFAULT AS IDENTITY NOT NULL ENABLE, 
    "TITLE" VARCHAR2(255 CHAR), 
    PRIMARY KEY ("CATEGORY_ID")
);


CREATE TABLE "SYS"."TICKET" 
(   "TICKET_ID" NUMBER(19,0) GENERATED BY DEFAULT AS IDENTITY NOT NULL ENABLE, 
    "CREATION_DATE" TIMESTAMP (6), 
    "DESCRIPTION" VARCHAR2(255 CHAR), 
    "PRIORITY" VARCHAR2(255 CHAR), 
    "STATUS" VARCHAR2(255 CHAR), 
    "TITLE" VARCHAR2(255 CHAR), 
    "CATEGORY_CATEGORY_ID" NUMBER(19,0), 
    "CREATED_BY_USERNAME" VARCHAR2(255 CHAR), 
    PRIMARY KEY ("TICKET_ID"),
    CONSTRAINT "FK_TICKET_CATEGORY" FOREIGN KEY ("CATEGORY_CATEGORY_ID") REFERENCES "SYS"."CATEGORY" ("CATEGORY_ID"),
    CONSTRAINT "FK_TICKET_USER" FOREIGN KEY ("CREATED_BY_USERNAME") REFERENCES "SYS"."USERSS" ("USERNAME")
);




CREATE TABLE "SYS"."COMMENTT" 
(   "COMMENT_ID" NUMBER(19,0) GENERATED BY DEFAULT AS IDENTITY NOT NULL ENABLE, 
    "COMMENT_TEXT" VARCHAR2(255 CHAR), 
    "CREATED_DATE" TIMESTAMP (6), 
    "USERNAME" VARCHAR2(255 CHAR), 
    "TICKET_ID" NUMBER(19,0), 
    PRIMARY KEY ("COMMENT_ID"),
    CONSTRAINT "FK_COMMENT_USERNAME" FOREIGN KEY ("USERNAME") REFERENCES "SYS"."USERSS" ("USERNAME"),
    CONSTRAINT "FK_COMMENT_TICKET" FOREIGN KEY ("TICKET_ID") REFERENCES "SYS"."TICKET" ("TICKET_ID")
);



CREATE TABLE "SYS"."AUDIT_LOG" 
(   "AUDIT_ID" NUMBER(19,0) GENERATED BY DEFAULT AS IDENTITY NOT NULL ENABLE, 
    "ACTION" VARCHAR2(255 CHAR), 
    "CHANGE_DATE" TIMESTAMP (6), 
    "CHANGED_BY" VARCHAR2(255 CHAR), 
    "NEW_VALUE" VARCHAR2(255 CHAR), 
    "OLD_VALUE" VARCHAR2(255 CHAR), 
    "TICKET_ID" NUMBER(19,0), 
    "COMMENT_TEXT" VARCHAR2(255 CHAR), 
    PRIMARY KEY ("AUDIT_ID")
);
